const Sidebar = (props) => {
  const onMenutoggleHandler = () => {
    props.onMenuToggle();
    console.log(props.isMenuOpen);
  };

  return (
    <aside className="sidebar">
      <div className="sidebar__header" onClick={onMenutoggleHandler}>
        <Brand />
      </div>
      <div className="sidebar__menuItems sidebar--scrollBar">
        <ul className="sidebar__items">
          {sideBarMenu.map((item, index) => (
            <li key={index}>
              <NavLink to={item.path} className={({ isActive, isPending }) => (isPending ? "pending" : isActive ? "active" : "")}>
                {item.icon}
                <span>{item.name}</span>
              </NavLink>
            </li>
          ))}
        </ul>
        <div className="sidebar-footer">
          <div className="notification">
            <img src={NotificationIcon} alt="Nottification" />
          </div>
          <div className="sidebar-user">
            <button type="button">
              <img src={userImg} alt="Nottification" />
            </button>
          </div>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
